#define MAX_TASKS	5
void task1_handler(void);
void task2_handler(void);
void task3_handler(void);
void task4_handler(void);


void init_systick_timer(uint32_t tick_hz);
__attribute__((naked)) void init_scheduler_stack(uint32_t sched_top_of_stack);
void init_tasks_stack(void);
void enable_processor_faults(void);
__attribute__((naked)) switch_sp_to_psp(void);
uint32_t get_psp_value(void);

void task_delay(uint32_t tick_count);

/* some stack memory calculation */
#define SIZE_TASK_STACK						1024U
#define SIZE_SCHED_STACK					1024U

#define SRAM_START							0X20000000U
#define SIZE_SRAM							( 125 * 1024 )
#define SRAM_END							(SRAM_START + SIZE_SRAM)

#define T1_STACK_START						SRAM_END
#define T2_STACK_START						(SRAM_END - 1 * SIZE_TASK_STACK)
#define T3_STACK_START						(SRAM_END - 2 * SIZE_TASK_STACK)
#define T4_STACK_START						(SRAM_END - 3 * SIZE_TASK_STACK)
#define IDLE_STACK_START					(SRAM_END - 4 * SIZE_TASK_STACK)
#define SCHED_STACK_START					(SRAM_END - 5 * SIZE_TASK_STACK)

#define TICK_HZ 1000U

#define HSI_CLOCK 16000000U
#define SYSTICK_TIM_CLK HSI_CLOCK

#define TASK_RUNNING_STATE 0x00
#define TASK_BLOCKED_STATE 0xFF


uint8_t current_task = 1; //task1 is runing
uint32_t g_tick_count = 0;

typedef struct
{
	uint32_t psp_value;
	uint32_t block_count;
	uint8_t current_state;
	void (*task_handler)(void);
}TCB_t;

TCB_t user_tasks[MAX_TASKS];

int main(void)
{
	enable_processor_faults();



	led_init_all();
	printf("Hello Task Scheduler\n");
	init_scheduler_stack(SCHED_STACK_START);

	init_tasks_stack();
	init_systick_timer(TICK_HZ);

	switch_sp_to_psp();

	task1_handler();
    /* Loop forever */
	printf("Hello World\n");
	for(;;);
}

void disable_irq_systick()
{
	uint32_t *pSCSR = (uint32_t*)0xE000E010;

	*pSCSR = *pSCSR & ~0x0002;
}

void enable_irq_systick()
{
	uint32_t *pSCSR = (uint32_t*)0xE000E010;

	*pSCSR = *pSCSR | 0x0002;
}

